mapper.select:

SELECT

from t_dfs_file_info
WHERE 1=1

  AND BUSI_TYPE = #{busiType,jdbcType=VARCHAR}


  AND COMPANY_TYPE = #{companyType,jdbcType=VARCHAR}


  AND COMPANY_CODE = #{companyCode,jdbcType=VARCHAR}


  AND LOCAL_FILENAME = #{localFilename,jdbcType=VARCHAR}


  AND DFS_FULL_FILENAME = #{dfsFullFilename,jdbcType=VARCHAR}


  AND REMARK = #{remark,jdbcType=VARCHAR}


  AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


  AND  date_format(LAST_UPD_TIME, '%Y%m%d') = date_format(#{lastUpdTime,jdbcType=TIMESTAMP}, '%Y%m%d')


  AND DFS_GROUPNAME = #{dfsGroupname,jdbcType=VARCHAR}


  AND STATUS = #{status,jdbcType=VARCHAR}


  AND OPERATOR = #{operator,jdbcType=VARCHAR}


  AND BANK_SETTLE_DATE = #{bankSettleDate,jdbcType=TIMESTAMP}


  AND DOCUMENT_TYPE = #{documentType,jdbcType=VARCHAR}


  AND DOCUMENT_TITLE = #{documentTitle,jdbcType=VARCHAR}


  AND CONN_CHANNEL = #{connChannel,jdbcType=VARCHAR}


  AND DOCUMENT_VERSION = #{documentVersion,jdbcType=VARCHAR}
