mapper.select:

select 

from t_pay_trans_detail
where PAY_TRANS_NO = #{payTransNo,jdbcType=VARCHAR}
  and SETTLE_DATE = #{settleDate,jdbcType=TIMESTAMP}

mapper.delete:

delete from t_pay_trans_detail
where PAY_TRANS_NO = #{payTransNo,jdbcType=VARCHAR}
  and SETTLE_DATE = #{settleDate,jdbcType=TIMESTAMP}
